import Link from "next/link";
import Image from "next/image";

export interface ProductProps {
  title: string;
  slug: string;
  price: string;
  featuredImage: string;
}

const Product = ({ title, slug, price, featuredImage }: ProductProps) => {
  return (
    <li className="product-card space-y-6">
      <Link href={`/products/${slug}`}>
        <Image
          src={featuredImage}
          alt={title}
          width={500}
          height={500}
          className="w-full"
          placeholder="blur"
          blurDataURL="data:image/jpeg;base64,"
        />
      </Link>

      <div>
        <p className="text-sm leading-6">{title}</p>
        <p className="text-function-dark-grey-default text-sm leading-6">
          {price}
        </p>
      </div>
    </li>
  );
};

export default Product;
